mapper.update:
UPDATE storage_service, cell
SET order_status = "OVERTIME", book_flag = 0
WHERE storage_service.cell_id = cell.id AND storage_service.order_no = #{order_no,jdbcType=VARCHAR};

UPDATE storage_service, cell
SET order_status = "EXPIRE", end_time = now(), final_duration = purchase_duration, cell.status = "EXPIRE", book_flag = 0
WHERE storage_service.cell_id = cell.id AND storage_service.order_no = #{order_no,jdbcType=VARCHAR};

mapper.select:
SELECT * FROM storage_service
WHERE order_no = #{order_no,jdbcType=VARCHAR}
AND keyt = #{keyt,jdbcType=VARCHAR}
AND terminal_id = #{terminal_id,jdbcType=INTEGER}

mapper.update:
UPDATE storage_service, cell
SET order_status = "RUNNING", begin_time = now(), cell.status = "USING", book_flag = 1
WHERE storage_service.cell_id = cell.id AND storage_service.order_no = #{order_no,jdbcType=VARCHAR};
